import React from 'react';
import ReactDOM from 'react-dom';

//How to add javascript inside the page with react?

const fName='Tim';
//{} => We add javascript inside the code using curly braceses
//shift +[] ={}

//create a number 10 and use a const
//Then display My favorite number us 10 (use javascript const to render)
const number = "10";
ReactDOM.render(

    <div>
   <h1> My name is Tim</h1>
   { /* A can add js variable using{}*/}
   <h1> My name is {fName}</h1>
   <h1> My number is 10</h1>
   <h1> My favoeite number is {number} </h1>
   <h1> My favoeite number is {6+10} </h1>
   <h1> My favoeite number is {Math.random()*10} </h1>
   { /* A can add function using{}*/}
    </div>
,
    document.getElementById("root"));
